<?phpinclude_once('inc/C_Base.php');include_once('models/catalog_model.php');class C_Page extends C_Base{	function __construct(){				parent::__construct();	}		public function action_index(){	    $text = 'Текст для нашего сайта';		$this->content = $this->Template('theme/v_index.php', array('text'=>$text));	}	public function action_lk(){		$this->title .= 'Личный кабинет';        $name = $_POST['name'];		$this->content = $this->Template('theme/v_edit.php', array('name' => $name));	}    public function action_entrance(){        $this->categoryTitle .= 'Вход';        $this->content = $this->Template('theme/v_entrance.php');    }    public function action_catalog(){        $test = $this->photos = getPhotos();        $this->content = $this->Template('theme/v_catalog.php', array('n'=> $test));    }}