<?phpinclude_once('inc/Controller.php');abstract class C_Base extends Controller{	protected $title = "Главная";	protected $catalogTitle = "Каталог";	protected $basketTitle = "Корзина";	protected $adminTitle = "Вход Администратора";    protected $mainTitle;    protected $categoryTitle;	protected $content;	protected $photos;	function __construct()	{		$this->title .= '';        $this->catalogTitle .= '';        $this->basketTitle .= '';        $this->adminTitle .= '';		$this->content .= '';		$this->photos = [];	}	public function render()	{		$vars = array('title' => $this->title, 'catalogTitle' => $this->catalogTitle, 'basketTitle'=>$this->basketTitle,                    'adminTitle'=>$this->adminTitle, 'mainTitle'=>$this->mainTitle, 'content' => $this->content);		$page = $this->Template('theme/v_main.php', $vars);						echo $page;	}	}