<?phpinclude_once('inc/C_Base.php');include_once('models/catalog_model.php');class C_Page extends C_Base{	function __construct(){				parent::__construct();	}		public function action_index(){	    $text = 'Текст для нашего сайта';		$this->content = $this->Template('theme/v_index.php', array('text'=>$text,));	}	public function action_lk(){	    $this->title = '';	    $this->basketTitle = '';        $this->catalogTitle = '';        $this->adminTitle = '';        $this->mainTitle = 'Кабинет Администратора';        $arrCatalog = $this->photos = getGoods();		$this->content = $this->Template('theme/v_admin.php', array('basket'=> $arrCatalog));	}    public function action_entrance(){        $this->content = $this->Template('theme/v_entrance.php');    }    public function action_catalog(){        $arrCatalog = $this->photos = getGoods();        $this->content = $this->Template('theme/v_catalog.php', array('arr'=> $arrCatalog));    }    public function action_fullPhoto(){        $arrFullPhoto = $this->photos = getGoods();        $this->content = $this->Template('theme/v_fullGoods.php', array('arr'=> $arrFullPhoto));    }    public function action_basket(){        $basket = $this->photos = getBasket();        $this->content = $this->Template('theme/v_basket.php', array('basket'=> $basket));    }    public function action_orders(){        $this->title = '';        $this->basketTitle = '';        $this->catalogTitle = '';        $this->adminTitle = '';        $this->mainTitle = 'Заказы';        $orders = $this->photos = getOrders();        $this->content = $this->Template('theme/v_order.php', array('orders'=> $orders));    }}